package org.apache.commons.lang3;

import static org.junit.jupiter.api.Assertions.assertArrayEquals;
import static org.junit.jupiter.api.Assertions.assertSame;

import org.junit.jupiter.api.Test;

/**
 * Tests for {@link ArrayFill}.
 * This class name is generated by the build process and is not ideal.
 * A better name would be ArrayFillTest.
 */
public class ArrayFillTestTest5 extends AbstractLangTest {

    @Test
    void fill_withCharArray_shouldModifyAndReturnSameArray() {
        // Arrange
        final char[] inputArray = new char[3];
        final char fillValue = 'X';
        final char[] expectedArray = {'X', 'X', 'X'};

        // Act
        final char[] resultArray = ArrayFill.fill(inputArray, fillValue);

        // Assert
        // 1. Verify that the method returns the same array instance for fluent chaining.
        assertSame(inputArray, resultArray, "The method should return the same array instance.");

        // 2. Verify that the array's contents have been correctly modified.
        assertArrayEquals(expectedArray, resultArray, "All elements should be filled with the specified value.");
    }
}