package org.apache.commons.collections4.bag;

import org.apache.commons.collections4.Bag;
import org.junit.Test;

import java.util.Collections;

import static org.junit.Assert.assertFalse;
import static org.junit.Assert.assertTrue;

/**
 * This test class contains improved tests for CollectionBag.
 * The original test was auto-generated by EvoSuite.
 */
public class CollectionBag_ESTestTest35 extends CollectionBag_ESTest_scaffolding {

    /**
     * Tests that calling retainAll with an empty collection removes all elements
     * from the bag and returns true, indicating the bag was modified.
     */
    @Test
    public void retainAllWithEmptyCollectionShouldRemoveAllElements() {
        // Arrange: Create a bag with one element and decorate it with CollectionBag.
        final Bag<Integer> underlyingBag = new HashBag<>();
        final Integer element = 2221;
        underlyingBag.add(element);

        final Bag<Integer> collectionBag = new CollectionBag<>(underlyingBag);

        // Act: Retain only the elements present in an empty collection.
        // This operation should effectively clear the bag.
        final boolean wasModified = collectionBag.retainAll(Collections.emptyList());

        // Assert: Verify the bag was modified and is now empty.
        assertTrue("retainAll should return true because an element was removed.", wasModified);
        assertTrue("The bag should be empty after retaining an empty collection.", collectionBag.isEmpty());
        assertFalse("The element should no longer be present in the bag.", collectionBag.contains(element));
    }
}