package org.jsoup.select;

import org.junit.Test;
import static org.junit.Assert.assertSame;

/**
 * Test for the {@link Elements#prepend(String)} method.
 * Note: This class contains a refactored test originally generated by EvoSuite,
 * focusing on improving clarity and maintainability.
 */
public class Elements_ESTestTest24 {

    /**
     * Verifies that calling prepend() with an empty string on an empty Elements
     * collection returns the same instance, ensuring method chaining is preserved.
     */
    @Test
    public void prependEmptyStringOnEmptyElementsIsChainable() {
        // Arrange: Create an empty Elements collection.
        Elements elements = new Elements();

        // Act: Call the prepend method with an empty string, which should be a no-op.
        Elements result = elements.prepend("");

        // Assert: The method should return the same instance to allow for method chaining.
        assertSame("The prepend() method should return 'this' to support chaining.", elements, result);
    }
}