package org.apache.commons.collections4.iterators;

import static org.junit.Assert.assertThrows;

import org.junit.Test;

/**
 * Contains tests for {@link FilterListIterator}.
 * This class focuses on improving the understandability of a test case
 * originally generated by an automated tool.
 */
public class FilterListIteratorTest {

    /**
     * Tests that calling next() on a FilterListIterator created with the
     * default constructor throws a NullPointerException because it has no
     * underlying iterator to delegate to.
     */
    @Test
    public void nextShouldThrowNullPointerExceptionWhenIteratorNotSet() {
        // Arrange: Create a FilterListIterator using the default constructor,
        // which leaves the underlying iterator as null.
        final FilterListIterator<Object> uninitializedIterator = new FilterListIterator<>();

        // Act & Assert: Calling next() should throw a NullPointerException.
        // The original auto-generated test expected a NoSuchElementException,
        // but inspection of the source code shows that an attempt to call a
        // method on the null iterator field results in a NullPointerException.
        assertThrows(NullPointerException.class, uninitializedIterator::next);
    }
}