package com.google.gson;

import org.junit.Test;
import static org.junit.Assert.*;

import java.util.List;

/**
 * This test class contains improved tests for the JsonArray class.
 * The original test, generated by EvoSuite, has been refactored for better readability.
 */
public class JsonArrayTest {

    /**
     * Tests that asList() returns a List view that correctly reflects the content
     * of a JsonArray containing a single element.
     */
    @Test
    public void asList_onArrayWithOneElement_returnsListWithCorrectSizeAndContent() {
        // Arrange: Create a JsonArray and add a single numeric element.
        JsonArray jsonArray = new JsonArray();
        Number numberToAdd = 0.0f;
        jsonArray.add(numberToAdd);

        // Act: Call the method under test to get a List view.
        List<JsonElement> listView = jsonArray.asList();

        // Assert: Verify that the returned list accurately reflects the JsonArray's content.
        // 1. Check the size of the list.
        assertEquals("The list view should have one element.", 1, listView.size());

        // 2. Check the content of the list.
        JsonElement expectedElement = new JsonPrimitive(numberToAdd);
        JsonElement actualElement = listView.get(0);
        assertEquals("The element in the list should match the one that was added.",
                     expectedElement, actualElement);
    }
}