package com.google.gson.internal.bind;

import com.google.gson.stream.JsonWriter;
import org.junit.Test;

import java.io.IOException;

import static org.junit.Assert.assertSame;

/**
 * This test class is a refactoring of a test originally generated by EvoSuite.
 * The focus of this test is on the fluent interface of the JsonTreeWriter.
 */
public class JsonTreeWriter_ESTestTest18 extends JsonTreeWriter_ESTest_scaffolding {

    /**
     * Tests that the value() method returns the same writer instance,
     * which is essential for supporting a fluent API (method chaining).
     */
    @Test
    public void value_returnsSameInstanceForChaining() throws IOException {
        // Arrange: Create a new JsonTreeWriter instance.
        JsonTreeWriter jsonTreeWriter = new JsonTreeWriter();

        // Act: Call the value() method.
        JsonWriter returnedWriter = jsonTreeWriter.value(0.0);

        // Assert: The returned object should be the same instance as the original writer.
        assertSame("The value() method must return 'this' to allow for method chaining.",
                jsonTreeWriter, returnedWriter);
    }
}