package org.threeten.extra.chrono;

import static org.junit.Assert.assertEquals;

import java.time.chrono.Era;
import org.junit.Test;

// The original test class name 'JulianChronology_ESTestTest5' and its extension
// 'JulianChronology_ESTest_scaffolding' suggest it was generated by a tool.
// These are kept for context, but would ideally be renamed in a real-world scenario.
public class JulianChronology_ESTestTest5 extends JulianChronology_ESTest_scaffolding {

    /**
     * Tests that for the AD era, the proleptic year is the same as the year-of-era.
     *
     * The proleptic year is a single timeline where year 1 AD is 1, and the year
     * before that is 0. For the AD era, the year-of-era and proleptic year are identical.
     */
    @Test
    public void prolepticYear_forAdEra_returnsSameYear() {
        // Arrange
        final JulianChronology julianChronology = JulianChronology.INSTANCE;
        final Era adEra = JulianEra.AD;
        final int yearOfEra = 1850;

        // Act
        int prolepticYear = julianChronology.prolepticYear(adEra, yearOfEra);

        // Assert
        assertEquals("For the AD era, proleptic year should equal year-of-era", yearOfEra, prolepticYear);
    }
}