package org.apache.commons.compress.archivers;

import org.junit.Test;

import java.nio.file.FileSystemException;

/**
 * Tests for the {@link Lister} command-line application.
 */
// The original test class name "Lister_ESTestTest3" and its base class
// "Lister_ESTest_scaffolding" suggest it was generated by the EvoSuite tool.
// For clarity, these would typically be renamed in a real-world scenario.
public class Lister_ESTestTest3 extends Lister_ESTest_scaffolding {

    /**
     * Verifies that Lister#go() throws a FileSystemException when the input
     * file path does not exist on the file system.
     */
    @Test(expected = FileSystemException.class)
    public void goShouldThrowExceptionWhenFileDoesNotExist() throws Exception {
        // Arrange: Provide command-line arguments with a non-existent file path.
        String[] argsWithNonExistentFile = {"path/to/a/non-existent-archive.zip"};
        Lister lister = new Lister(true, argsWithNonExistentFile);

        // Act & Assert: Attempting to list the archive should fail with a
        // FileSystemException because the file cannot be found.
        lister.go();
    }
}