package org.apache.commons.lang3.concurrent;

import org.junit.Test;
import static org.junit.Assert.assertTrue;

/**
 * Unit tests for {@link MultiBackgroundInitializer}.
 *
 * Note: The original test class name "MultiBackgroundInitializer_ESTestTest15"
 * was generated by a tool. A more conventional name is used here for clarity.
 */
public class MultiBackgroundInitializerTest {

    /**
     * Tests that calling initialize() on a MultiBackgroundInitializer with no
     * child initializers results in a successful outcome.
     */
    @Test
    public void testInitializeWithNoChildInitializersIsSuccessful() throws Exception {
        // Arrange: Create an instance of MultiBackgroundInitializer without adding any child initializers.
        MultiBackgroundInitializer multiInitializer = new MultiBackgroundInitializer();

        // Act: Call the initialize() method, which should complete immediately as there are no tasks.
        MultiBackgroundInitializer.MultiBackgroundInitializerResults results = multiInitializer.initialize();

        // Assert: Verify that the initialization process is marked as successful.
        assertTrue("Initialization of an empty MultiBackgroundInitializer should be successful", results.isSuccessful());
    }
}