package org.jsoup.nodes;

import org.jsoup.parser.Parser;
import org.junit.Test;
import static org.junit.Assert.assertFalse;

/**
 * Test suite for {@link NodeIterator}.
 * This specific test class focuses on scenarios generated by EvoSuite, refactored for clarity.
 */
public class NodeIterator_ESTestTest3 { // Note: A more conventional name would be NodeIteratorTest

    /**
     * Verifies that hasNext() returns false when iterating over a document
     * that does not contain any nodes of the specified type.
     */
    @Test
    public void hasNextShouldReturnFalseWhenNoMatchingNodesExist() {
        // Arrange: Create a document that is guaranteed not to have any <form> elements.
        Document document = Parser.parseBodyFragment("<div><p>No forms here.</p></div>", "");
        
        // Act: Create an iterator that specifically looks for FormElement nodes.
        NodeIterator<FormElement> formElementIterator = new NodeIterator<>(document, FormElement.class);

        // Assert: The iterator should immediately report that there are no matching elements.
        assertFalse("Iterator should not find any elements", formElementIterator.hasNext());
    }
}