package com.itextpdf.text.pdf;

import org.junit.Test;
import static org.junit.Assert.assertSame;

/**
 * Test suite for the {@link PdfDictionary} class.
 * This class contains tests that were improved for clarity and maintainability.
 */
// The original test extended a scaffolding class generated by EvoSuite.
// This dependency is maintained for potential environment setup, but the test logic
// itself is self-contained.
public class PdfDictionary_ESTestTest7 extends PdfDictionary_ESTest_scaffolding {

    /**
     * Verifies that getDirectObject() correctly retrieves an object
     * that was previously added to the dictionary.
     *
     * This test checks the fundamental put/get functionality of the PdfDictionary.
     */
    @Test
    public void getDirectObjectShouldReturnPreviouslyAddedObject() {
        // Arrange: Create a dictionary and a key-value pair to insert.
        PdfDictionary dictionary = new PdfDictionary();
        PdfName key = new PdfName("MyKey");
        PdfName value = new PdfName("MyValue");

        // Act: Insert the key-value pair and then retrieve the value using the key.
        dictionary.putEx(key, value);
        PdfObject retrievedObject = dictionary.getDirectObject(key);

        // Assert: Verify that the retrieved object is the same as the original value.
        assertSame("The retrieved object should be the exact same instance as the one that was put in.",
                value, retrievedObject);
    }
}