package org.apache.commons.io;

import org.junit.Test;
import static org.junit.Assert.assertThrows;
import java.io.FileNotFoundException;

/**
 * Contains tests for the {@link RandomAccessFileMode} enum.
 * This class was originally generated by EvoSuite and has been improved for clarity.
 */
public class RandomAccessFileMode_ESTestTest34 extends RandomAccessFileMode_ESTest_scaffolding {

    /**
     * Tests that calling {@link RandomAccessFileMode#create(String)} in read-only ("r") mode
     * for a file that does not exist throws a {@link FileNotFoundException}.
     * This is the correct behavior, as read-only mode should not create new files.
     */
    @Test
    public void createWithReadOnlyModeForNonExistentFileThrowsFileNotFoundException() {
        // Arrange: Get the read-only mode and define a path for a file that does not exist.
        final RandomAccessFileMode readOnlyMode = RandomAccessFileMode.valueOfMode("r");
        final String nonExistentFileName = "non_existent_file.txt";

        // Act & Assert: Verify that attempting to create a RandomAccessFile for a
        // non-existent file in read-only mode throws the expected exception.
        assertThrows(FileNotFoundException.class, () -> {
            readOnlyMode.create(nonExistentFileName);
        });
    }
}