package org.locationtech.spatial4j.shape;

import org.junit.Test;
import org.locationtech.spatial4j.context.SpatialContext;

import java.util.ArrayList;
import java.util.List;

import static org.junit.Assert.assertEquals;

/**
 * Unit tests for the {@link ShapeCollection} class.
 *
 * Note: The original test was auto-generated by a tool and has been
 * refactored for clarity and maintainability.
 */
public class ShapeCollectionTest {

    /**
     * Verifies that the area of an empty ShapeCollection is zero.
     */
    @Test
    public void getArea_ofEmptyCollection_shouldReturnZero() {
        // Arrange: Create an empty collection of shapes.
        List<Shape> emptyShapes = new ArrayList<>();
        SpatialContext geoContext = SpatialContext.GEO;
        ShapeCollection<Shape> emptyCollection = new ShapeCollection<>(emptyShapes, geoContext);

        // Act: Calculate the area of the empty collection.
        double area = emptyCollection.getArea(geoContext);

        // Assert: The calculated area should be exactly 0.0.
        assertEquals(0.0, area, 0.0);
    }
}