package org.jsoup.parser;

import org.jsoup.nodes.Attributes;
import org.junit.Test;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.fail;

/**
 * This test class contains an improved version of a test originally generated by EvoSuite.
 * The original test verified exception handling in the XmlTreeBuilder.
 */
public class XmlTreeBuilder_ESTestTest39 { // Note: Original class name is kept for context.

    /**
     * Verifies that processing a StartTag token with a null tag name
     * throws an IllegalArgumentException.
     */
    @Test
    public void processStartTagWithNullNameThrowsIllegalArgumentException() {
        // Arrange: Create a builder and a start token with a null name.
        XmlTreeBuilder xmlTreeBuilder = new XmlTreeBuilder();
        xmlTreeBuilder.parse("", "https://example.com/"); // Initialize builder's internal state.

        Token.StartTag startTagWithNullName = new Token.StartTag();
        startTagWithNullName.nameAttr(null, new Attributes()); // This is the invalid state we want to test.

        // Act & Assert: Attempt to process the invalid token and verify the exception.
        try {
            xmlTreeBuilder.process(startTagWithNullName);
            fail("Expected an IllegalArgumentException to be thrown for a null tag name, but it was not.");
        } catch (IllegalArgumentException e) {
            // This is the expected outcome.
            assertEquals("String must not be empty", e.getMessage());
        }
    }
}